const initialState = {
	start: false,
	success: false,
	books: [],
	fail: false,
	errorMessage: "",
};

const booksReducer = (state = initialState, action) => {
	switch (action.type) {
		case "FETCH_BOOKS_START":
			return {
				...state,
				start: true,
			};
		case "FETCH_BOOKS_SUCCESS":
			return {
				...state,
				start: false,
				success: true,
				books: action.payload,
			};
		case "FETCH_BOOKS_FAIL":
			return {
				...state,
				start: false,
				fail: true,
				errorMessage: action.payload,
			};

		default:
			return state;
	}
};

export default booksReducer;
