const initialState = {
	start: false,
	success: false,
	categories: [],
	fail: false,
	errorMessage: "",
};

const categoriesReducer = (state = initialState, action) => {
	switch (action.type) {
		case "FETCH_CATEGORIES_START":
			return {
				...state,
				start: true,
			};

		case "FETCH_CATEGORIES_SUCCESS":
			return {
				...state,
				start: false,
				success: true,
				categories: action.payload,
			};

		case "FETCH_CATEGORIES_FAIL":
			return {
				...state,
				start: false,
				fail: true,
				errorMessage: action.payload,
			};
		default:
			return state;
	}
