import React, { useEffect, useState } from "react";
import axios from "axios";
import Loading from "./Loading";

const AddBookForm = (props) => {
	const [categories, setCategories] = useState(null);

	useEffect(() => {
		axios
			.get("http://localhost:3004/categories")
			.then((res) => {
				console.log(res);
				setCategories(res.data);
			})
			.catch((err) => console.log(err));
	}, []);

	if (categories === null) {
		return <Loading />;
	}

	return (
		<div className="container my-5">
			<form>
				<div className="row my-5">
					<div className="col">
						<input
							type="text"
							className="form-control"
							placeholder="Book Name"
						/>
					</div>
					<div className="col">
						<input
							type="text"
							className="form-control"
							placeholder="Author Name"
						/>
					</div>
				</div>
				<div className="row my-5">
					<div className="col">
						<input type="text" className="form-control" placeholder="ISBN No" />
					</div>
					<div className="col">
						<select className="form-select input-bg">
							<option selected> Select Category </option>
							{categories.map((cat) => {
								return (
									<option value={cat.id} style={{ backgroundColor: "white" }}>
										{" "}
										{cat.name}{" "}
									</option>
								);
							})}
						</select>
					</div>
					<div className="mt-5 d-flex justify-content-end w-50">
						<button className="btn btn-success p-2"> SAVE NEW BOOK </button>
					</div>
				</div>
			</form>
		</div>
	);
};

export default AddBookForm;
