import React, { useState, useEffect } from "react";
import { useNavigate } from "react-router-dom";
import { useSelector, useDispatch } from "react-redux";
import axios from "axios";
import Loading from "./Loading";
import Categories from "./Categories";

const AddCategoryForm = (props) => {
	const dispatch = useDispatch();
	const { categoriesState } = useSelector((state) => state);
	console.log(categoriesState);
	const [newCategoryName, setNewCategoryName] = useState("");
	const navigate = useNavigate();

	const handleSubmit = (event) => {
		event.preventDefault();
		if (newCategoryName === "" || newCategoryName === Categories.name) {
			alert("Kategori alanı boş veyahut mevcut kategorilerle aynı olamaz");
			return;
		}

		const newCategory = {
			id: new Date().getTime(),
			categoryName: newCategoryName,
		};

		axios
			.post(" http://localhost:3004/categories", newCategory)
			.then((res) => {
				alert(" Kategori Başarıyla Kaydedildi", 2000);
				dispatch({ type: "ADD_CATEGORY", payload: newCategory });
				setNewCategoryName("");
				navigate("/add-category");
			})
			.catch((err) => console.log(err));
	};

	if (categoriesState.success !== true) {
		return <Loading />;
	}

	return (
		<div className="container my-5">
			<form
				onSubmit={handleSubmit}
				className="d-flex justify-content-between align-items-center row"
				style={{ width: "800px" }}
			>
				<div className="col" style={{ minWidth: "100px" }}>
					<span className="text-center"> NEW CATEGORY NAME: </span>
				</div>
				<div className="col" style={{ minWidth: "350px" }}>
					<div className="">
						<input
							type="text"
							className="form-control"
							placeholder="New Category Name"
							value={newCategoryName}
							onChange={(event) => setNewCategoryName(event.target.value)}
						/>
					</div>
				</div>

				<div className="col " style={{ minWidth: "250px" }}>
					<button type="submit" className="btnEdit btn btn-success p-2">
						{" "}
						SAVE NEW CATEGORY{" "}
					</button>
				</div>
			</form>
		</div>
	);
};

export default AddCategoryForm;
